    /**
     * 
     * @param estimates
     * @param bookNames
     * @param wordCount 
     */
    public void textSetting(int estimates [], String bookNames [], int wordCount []){
        bookOneNameLabel.setText(bookNames[0]);
        bookTwoNameLabel.setText(bookNames[1]);
        bookThreeNameLabel.setText(bookNames[2]);
        wordCountBookOneLabel.setText(wordCount[0] + "");
        wordCountBookTwoLabel.setText(wordCount[1] + "");
        wordCountBookThreeLabel.setText(wordCount[2] + "");
        int offByBookOne = Math.abs(estimates[0]-wordCount[0]);
        int offByBookTwo = Math.abs(estimates[1]-wordCount[1]);
        int offByBookThree = Math.abs(estimates[2]-wordCount[2]);
        offByBookOneLabel.setText(offByBookOne + "");
        offByBookTwoLabel.setText(offByBookTwo + "");
        offByBookThreeLabel.setText(offByBookThree + "");
    }
    
    /**
     * Method to get the user inputs
     * @return estimates from the user
     */
    public int [] parsing(){
        int [] estimates = new int [3];
        try{
            estimates[0] = Integer.parseInt(estimateBookOneTextField.getText());
        }
        catch (NumberFormatException e){
            System.err.println("That is not a valid input for book 1 estimate.");
        }
        try {
            estimates[1] = Integer.parseInt(estimateBookTwoTextField.getText());
        }
        catch (NumberFormatException e){
            System.err.println("That is not a valid input for book 2 estimate.");
        }
        try {
            estimates[2] = Integer.parseInt(estimateBookThreeTextField.getText());
        }
        catch (NumberFormatException e){
            System.err.println("That is not a valid input for book 3 estimate.");
        }
        return estimates;
    }
