    private void startButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_startButtonActionPerformed
        //Arrays
        String bookNames [] = {"Fellowship of the Ring","The Two Towers","The Return of the King"};
        int wordCount [] = {177227, 143436, 134462};
        int estimates [];
        //Methods
        estimates = parsing();
        textSetting(estimates,bookNames,wordCount);
    }//GEN-LAST:event_startButtonActionPerformed

